/* Variables */

:root {
   --red: #E50914;
   --fullBlack: #000;
   --black: #121212; /* For navbars */
   --darkGray: #1A1A1A; /* For text boxes */
   --gray: #424242; /* For less important buttons */
   --lightGray: #8C8787; /* For navbar text */
   --evenLighterGray: #C4C4C4; /* For buttons and secondary text */
   --white: #fff;
}

/* Predefined styles */

.title {
   font-style: normal;
   font-weight: 700;
   font-size: 1.3rem;
}

.btn {
   border: 0;
   border-radius: .2rem;
   background-color: var(--evenLighterGray);
   font-size: .9rem;
   font-weight: 700;

   padding: 1rem 1.5rem;

   cursor: pointer;

   transition: 500ms ease all;
}

.hidden {
   display: none;
}

.owl-theme .owl-dots {
   display: none;
}

/* Scroll indicator */

.scroll-down.arrows span {
   position: absolute;
   display: inline-block;
   top: 20px;
   height: 25px;
   width: 2px;
   left: calc(50% - 1px);
   animation: animateArrows 1.25s infinite linear;
}

@keyframes animateArrows {
   0%, 40%, 100% {
      opacity: 0;
   }
   75% {
      opacity: 1;
   }
}

.scroll-down.arrows span:first-child {
   top: 35px;
   animation-delay: 0s;
}

.scroll-down.arrows span:nth-child(2) {
   top: 35px;
   animation-delay: .33s;
}

.scroll-down.arrows span:last-child {
   top:  50px;
   animation-delay: .66s;
}

.scroll-down.arrows span::before, .scroll-down.arrows span::after {
   position: absolute;
   content: "";
   width: 3px;
   height: 100%;
   top: 0;
   left: 0;
   background: #fff;
   border-radius: 3px;
}

.scroll-down.arrows span::before {
   transform-origin: bottom;
   transform: rotate(-45deg);
}

.scroll-down.arrows span::after {
   transform-origin: bottom;
   margin-left: -1px;
   transform: rotate(45deg);
}

