   margin: 0;
   padding: 0;
   box-sizing: border-box;

   scroll-behavior: smooth;
}

body {
   background-color: var(--fullBlack);
   font-family: 'Source Sans Pro', sans-serif;
   color: var(--white);
}

header {
   padding: 20px 0;
   background-color: var(--black);
}

header .container {
   display: flex;
   justify-content: space-between;
   align-items: center;
