   display: flex;
}

header .navbar a{
   color: var(--lightGray);

   text-decoration: none;
   
   text-align: center;

   margin-right: 2rem;

   transition: 250ms ease all;
}

header .navbar a:last-child{
   margin-right: 3em;
}

header .navbar a:hover {
   color: var(--white);
}

header .navbar a i{
   font-size: 1.5em;
}

