main .highlight {
   background: linear-gradient(180deg, rgba(0, 0, 0, 0.3) 0%, rgba(0, 0, 0, 0.6) 90%, #000 100%), url("/5454efde9b8c3d00dae1e5c6531f3bbe.png");
   background-size: cover;
   background-position: center;
   display: flex;
   flex-direction: column;
   justify-content: center;
   align-items: flex-start;
   height: 75vh;
}

main .highlight .container {
   width: 60vw;

   margin-left: 3rem;
}

main .highlight .title {
   font-size: 3em;
   line-height: 3rem;
   margin-bottom: 1rem;
}

main .highlight .description {
   margin-bottom: 1.5em;
}

#play {

}

#play i {
   padding-right: 0.5rem;
}

#play .btn-play {
   margin-right: 1.5rem;

}

#play .btn-info {

}

#play .btn {
   background-color: rgba(0, 0, 0, .4);
   color: var(--white);
   
   opacity: 80%;

}

#play .btn:hover {
   background-color: var(--white);
   color: var(--black);

   opacity: 100%;
}

/* Carrousels */

#home {
   position: relative;

   margin-top: 10vh;

   height: 100%;
   width: 100%;
}

#home .darken {
   box-shadow: inset 0 0 20px 15px var(--fullBlack);
   z-index: 50;

   position: absolute;

   pointer-events: none;

   width: 100%;
   height: 100%;
}

#scroll {
   position: absolute;
   left: 50%;
   transform: translateX(-50%);

   top: -15vh;
}

#home .title {
   padding: 1rem 2rem;
}

#home .explorer {
   padding-bottom: 5vh;
}

#film-row {
   margin-bottom: 5vh;
}

/* Media queries */

@media (max-width: 700px) {
   header {
      height: 50%;
   }

   header .logo {
      display: none;
   }

   header .container {
      justify-content: center;
   }

   header .navbar {
      justify-content: space-around;
   }

   header .navbar a {
      font-size: 0.9rem;
   }

   header .navbar a:last-child {
      margin: 0;
   }

   main .highlight {
      justify-content: center;
      align-items: center;
   }

   main .highlight .title {
      font-size: 2.5rem;
   }

   main .highlight .container {
      margin-left: 0;
      width: 90vw;
      transform: scale(80%);
   }

   #play {
      display: flex;
      flex-wrap: nowrap;
   }

   #play button {
      font-size: 0.8rem;
   }
}

